def main():
    Settings.set_chromosome_bits("exercise2", len(get_objects()))

    n = 200

    print("\nGreedy: ")
    time, elementos, volumen, price = greedy.execute()
    acum_time = 0
    for i in range(n):
        time, elementos, volumen, price = greedy.execute()
        acum_time += time
    avg = acum_time / n
    show(time, elementos, volumen, price, avg, n)

    print("\nExhaustiva: ")
    n = 1
    time, elementos, volumen, price = exhaustiva.execute()
    acum_time = 0
    for i in range(n):
        time, elementos, volumen, price = exhaustiva.execute()
        acum_time += time
    avg = acum_time / n
    show(time, elementos, volumen, price, avg, n)

    print("\nAlgoritmos geneticos: ")
    time, elementos, volumen, price = api.execute()
    acum_time = 0
    for i in range(n):
        time, elementos, volumen, price = api.execute()
        acum_time += time
    avg = acum_time / n
    show(time, elementos, volumen, price, avg, n)




def show(time, objets, volumen, price, acum, n):
    print("Objetos: {}".format(objets))
    print("Precio final: {}".format(price))
    print("Volumen final: {}".format(volumen))
    print("Tiempo de ejecucion una vez: {0:.6f}".format(time))
    print("Tiempo de ejecucion prmedio luego de {0} veces: {1:.6f}".format(n,acum))

if __name__ == '__main__':
    import exercise2.api as api
    import exercise2.greedy as greedy
    import exercise2.exhaustiva as exhaustiva
    from base.logic.settings_manager import Settings
    
    main()
